mapper.update:
update user
set profile_photo_url = #{toProfilePhotoUrl}
where seq = #{userSeq}

mapper.select:
select count(1) > 0
from user
where nickname = #{toNickname}
  and seq != #{userSeq}

mapper.update:
update user
set nickname = #{toNickname}
where seq = #{userSeq}

update user
set age_group = #{toAgeGroup}
where seq = #{userSeq}

update user
set introduce = #{toIntroduce}
where seq = #{userSeq}

mapper.select:
select count(1) > 0
from review
where seq = #{reviewSeq}
  and customer_seq = #{userSeq}
